import React,{ Component } from 'react'
import './App.css';

class App extends Component {
  constructor(){
    super()
    this.state ={
      todos: [
        {
          id: 0,
          title: "tets",
          done: false
        },
        {
          id: 1,
          title: "test2",
          done: true
        }
      ]
    }
  }
  changeState(clickTodo) {
    const a = this.state.todo[clickTodo.index];
    a.done = !a.done;
    console.log("sa");

  }
  render(){
    return (
      <div className = "App">
        <h2> TodoList </h2>
        <TodoList
          todos = {this.state.todos}
          changeState = {this.changeState.bind(this)}
        />
      </div>
    );
  }
}

class TodoList extends Component {
  render() {
    const todos = this.props.todos.map( todo =>
      <Todo
        key = {todo.id}
        index = {todo.index}
        id = {todo.id}
        title = {todo.title}
        done = {todo.done}
        changeState = {this.props.changeState}
      />
    )
    return(
      <ul>
        {todos}
      </ul>
    )

  }
}
class Todo extends Component {
  render() {
    const link = this.props.done ? '元に戻す' : '完了'
    return(
      <li className="Cell">
        <span> {this.props.id} </span>
        <span> {this.props.title} </span>
        <a href="#/" onClick={(e)=>{ e.preventDefault(); this.props.changeState(this.props.index)}}> {link} </a>
      </li>
    );
  }
}

export default App;
