import React,{ Component } from 'react'
import './App.css';

class App extends Component {
  constructor(){
    super()
    this.state ={
      todos: [
        {
          id: 0,
          title: "tets",
          done: false
        },
        {
          id: 1,
          title: "test2",
          done: true
        }
      ]
    }
  }
  render(){
    return (
      <div className = "App">
        <h2> TodoList </h2>
        <TodoList
          todos={this.state.todos}
        />
      </div>
    );
  }
}

class TodoList extends Component {
  render() {
    const todos = this.props.todos.map( todo =>
      <Todo
        key={todo.id}
        {...todo}
      />
    )
    return(
      <ul>
        {todos}
      </ul>
    )

  }
}
class Todo extends Component {
  render() {
    const link = this.props.done ? '元に戻す' : '完了'
    return(
      <li className="Cell">
        <span> {this.props.id} </span>
        <span> {this.props.title} </span>
        <a href="" onClick={(e)=>{changeState(this.props)}} > {link} </a>
      </li>
    );
  }
}

function changeState(clickTodo) {
  const todos = this.state.todos.slice();
  const todo = todos[clickTodo.index];
  todo.done = !todo.done;
}

export default App;
